void error_msg() {
	std::cerr << "*** Program terminated due to the following error ***"<< std::endl;
}

void silent_line(const char &str, const int &it){
	std::cout << "/*";
	for (int i =0; i!=it; i++){
		std::cout << str;
	}
	std::cout << "*/" << std::endl;
}

void print_msg(const char &str){
	std::cout << str << std::endl;
}

void print_msg(const char &str, const int &numchar){
	for (int i = 0; i!= numchar; i++){
		std::cout << str;
	}
	std::cout << std::endl;
}

